public class B17298_오큰수 { 	public static void main(String[] args) throws IOException { 		BufferedReader br = new BufferedReader(new InputStreamReader(System.in)); 		int N = Integer.parseInt(br.readLine()); 		int A [] = new int [N]; 		StringTokenizer st = new StringTokenizer(br.readLine()); 		Stack<Integer> s = new Stack<>(); 		StringBuilder sb = new StringBuilder(); 		 		for(int i=0; i<N; i++) A[i] = Integer.parseInt(st.nextToken()); 		 		for(int i=0; i<N; i++) { 			while(!s.isEmpty() && A[s.peek()] < A[i]) A[s.pop()] = A[i]; 			 			s.add(i); 		} 		 		while(!s.isEmpty()) A[s.pop()] = -1; 		 		for(int i=0; i<N; i++) sb.append(A[i]).append(" "); 		System.out.println(sb); 	} }
